// file: frontend/src/CMCMarketData.jsx
import React, { useState, useEffect } from "react";
import { Card, Table } from "react-bootstrap";
import { Sparklines, SparklinesLine } from "react-sparklines";

function CMCMarketData() {
  const [coins, setCoins] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/market_data")
      .then(resp => {
        if (!resp.ok) {
          throw new Error("Failed to fetch /api/market_data");
        }
        return resp.json();
      })
      .then(data => {
        if (data.status === "ok") {
          setCoins(data.coins || []);
        } else {
          setError(data.msg || "Error returned from server");
        }
      })
      .catch(err => {
        setError(err.message);
      });
  }, []);

  return (
    <Card className="mt-4">
      <Card.Body>
        <Card.Title>CMC Top 10 Listing</Card.Title>
        {error && <p className="text-danger fw-bold">{error}</p>}

        <Table bordered hover responsive className="align-middle">
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
              <th>Symbol</th>
              <th>Price (USD)</th>
              <th>Market Cap</th>
              <th>7-Day Trend</th>
            </tr>
          </thead>
          <tbody>
            {coins.map((coin, idx) => {
              const { name, symbol, quote, sparkline } = coin;
              const price = quote.USD.price;
              const mcap = quote.USD.market_cap;
              return (
                <tr key={coin.id}>
                  <td>{idx+1}</td>
                  <td>{name}</td>
                  <td>{symbol}</td>
                  <td>${price.toFixed(2)}</td>
                  <td>${mcap.toLocaleString()}</td>
                  <td style={{width:"120px"}}>
                    {sparkline && sparkline.every(x => x!==null) ? (
                      <Sparklines data={sparkline} width={100} height={30}>
                        <SparklinesLine color="green" />
                      </Sparklines>
                    ) : (
                      <span className="text-muted">No Data</span>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </Card.Body>
    </Card>
  );
}

export default CMCMarketData;
