// file: frontend/src/CMCMarketData.jsx
import React, { useState, useEffect } from "react";

function CMCMarketData() {
  const [coins, setCoins] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    // In production, hide the API key somewhere safe, or call your own backend
    const cmcKey = "37a18e4d-f319-49ed-8e8b-de0fb1c06cda"; 
    const url = "https://pro-api.coinmarketcap.com/v1/cryptocurrency/listings/latest?limit=5"; 
    
    fetch(url, {
      headers: {
        "X-CMC_PRO_API_KEY": cmcKey,
      }
    })
      .then(resp => {
        if (!resp.ok) throw new Error("Failed to fetch CMC data");
        return resp.json();
      })
      .then(data => {
        if (data && data.data) {
          setCoins(data.data);
        }
      })
      .catch(err => {
        setError(err.message);
      });
  }, []);

  return (
    <div style={{ marginTop: "2rem" }}>
      <h2 style={{ textAlign:"center" }}>CMC Top 5 Listing</h2>
      {error && <p className="error-msg">{error}</p>}

      <table style={{ width:"100%", marginTop:"1rem", borderCollapse:"collapse" }}>
        <thead>
          <tr style={{ background:"#eee" }}>
            <th style={{ padding:"0.5rem", textAlign:"left" }}>#</th>
            <th style={{ padding:"0.5rem", textAlign:"left" }}>Name</th>
            <th style={{ padding:"0.5rem", textAlign:"left" }}>Symbol</th>
            <th style={{ padding:"0.5rem", textAlign:"left" }}>Price</th>
            <th style={{ padding:"0.5rem", textAlign:"left" }}>Market Cap</th>
          </tr>
        </thead>
        <tbody>
          {coins.map((coin, idx) => (
            <tr key={coin.id} style={{ borderBottom:"1px solid #ccc" }}>
              <td style={{ padding:"0.5rem" }}>{idx+1}</td>
              <td style={{ padding:"0.5rem" }}>{coin.name}</td>
              <td style={{ padding:"0.5rem" }}>{coin.symbol}</td>
              <td style={{ padding:"0.5rem" }}>
                ${Number(coin.quote.USD.price).toFixed(2)}
              </td>
              <td style={{ padding:"0.5rem" }}>
                ${Number(coin.quote.USD.market_cap).toLocaleString()}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CMCMarketData;
