// PredictForm.jsx

import React, { useState } from "react";
import axios from "axios";

function PredictForm() {
  const [coin, setCoin] = useState("ADA");    // default
  const [horizon, setHorizon] = useState(7);  // default
  const [apiKey, setApiKey] = useState("");   // or store it in .env
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  async function handleSubmit() {
    try {
      setError(null);
      setResult(null);

      // POST to Flask at /api/predict
      const resp = await axios.post("/api/predict", {
        coin: coin,
        horizon: horizon,
        api_key: apiKey
      });
      setResult(resp.data);
    } catch (err) {
      console.error(err);
      if (err.response) {
        // Server error
        setError(err.response.data.error);
      } else {
        // e.g. network or other
        setError("Request failed.");
      }
    }
  }

  return (
    <div style={{ margin: "2rem" }}>
      <h2>Crypto Prediction</h2>

      <div>
        <label>Coin: </label>
        <select value={coin} onChange={(e) => setCoin(e.target.value)}>
          <option value="ADA">ADA</option>
          <option value="BTC">BTC</option>
          <option value="ETH">ETH</option>
          <option value="DOGE">DOGE</option>
          <option value="SHIB">SHIB</option>
          <option value="DOT">DOT</option>
          <option value="SOL">SOL</option>
          <option value="XRP">XRP</option>
          <option value="BNB">BNB</option>
          {/* etc. */}
        </select>
      </div>

      <div>
        <label>Horizon (days): </label>
        <input
          type="number"
          value={horizon}
          onChange={(e) => setHorizon(e.target.value)}
          style={{ width: "80px" }}
        />
      </div>

      <div>
        <label>CMC API Key: </label>
        <input
          type="text"
          placeholder="Enter key"
          value={apiKey}
          onChange={(e) => setApiKey(e.target.value)}
          style={{ width: "300px" }}
        />
      </div>

      <button onClick={handleSubmit}>Predict</button>

      {error && (
        <div style={{ color: "red", marginTop: "1rem" }}>
          Error: {error}
        </div>
      )}

      {result && !error && (
        <div style={{ marginTop: "1rem" }}>
          <p>Coin: {result.coin}</p>
          <p>Horizon: {result.horizon} day(s)</p>
          <p>Predicted Price: {result.predicted_price}</p>
          <p>Model Used: {result.model_used}</p>
        </div>
      )}
    </div>
  );
}

export default PredictForm;
