// file: frontend/src/PredictForm.jsx
import React, { useState } from "react";

const PredictForm = () => {
  const [coin, setCoin] = useState("ADA");
  const [horizon, setHorizon] = useState("7");
  const [apiKey, setApiKey] = useState("");
  const [predicted, setPredicted] = useState("");
  const [errorMsg, setErrorMsg] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setPredicted("");
    setErrorMsg("");

    try {
      const resp = await fetch("http://localhost:5000/api/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          coin: coin,
          horizon: horizon,
          cmc_api_key: apiKey
        }),
      });
      if (!resp.ok) {
        const errData = await resp.json();
        throw new Error(errData.error || "Request failed");
      }
      const data = await resp.json();
      if (data.status === "ok") {
        setPredicted(`Predicted Price = $${data.predicted_price.toFixed(4)}`);
      } else {
        setErrorMsg("Prediction error: " + JSON.stringify(data));
      }
    } catch (err) {
      setErrorMsg(err.message || "Something went wrong");
    }
  };

  return (
    <div style={{ margin: "20px" }}>
      <h2>Crypto Prediction</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Coin &nbsp;</label>
          <select value={coin} onChange={(e) => setCoin(e.target.value)}>
            <option value="ADA">ADA</option>
            <option value="Cardano">Cardano</option>
            <option value="BTC">BTC</option>
            <option value="Bitcoin">Bitcoin</option>
            <option value="ETH">ETH</option>
            <option value="Dogecoin">DOGE</option>
            <option value="SHIB">SHIB</option>
            <option value="DOT">DOT</option>
            <option value="SOL">SOL</option>
          </select>
        </div>
        <div>
          <label>Horizon (days)&nbsp;</label>
          <input
            type="number"
            value={horizon}
            onChange={(e) => setHorizon(e.target.value)}
          />
        </div>
        <div>
          <label>CMC API Key:&nbsp;</label>
          <input
            type="text"
            value={apiKey}
            onChange={(e) => setApiKey(e.target.value)}
          />
        </div>
        <button type="submit">Predict</button>
      </form>

      {predicted && <p style={{ color: "green" }}>{predicted}</p>}
      {errorMsg && <p style={{ color: "red" }}>{errorMsg}</p>}
    </div>
  );
};

export default PredictForm;
