// file: frontend/src/PredictForm.jsx
import React, { useState } from "react";

function PredictForm() {
  const [coin, setCoin] = useState("ADA");
  const [horizon, setHorizon] = useState("7");
  const [apiKey, setApiKey] = useState("");
  const [result, setResult] = useState(null);
  const [errorMsg, setErrorMsg] = useState("");

  async function handleSubmit(e) {
    e.preventDefault();
    setResult(null);
    setErrorMsg("");

    try {
      const resp = await fetch("http://localhost:5000/api/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          coin: coin,
          horizon: horizon,
          api_key: apiKey
        })
      });

      const data = await resp.json();
      if (!resp.ok) {
        // some error
        setErrorMsg(data.error || "Request failed");
        // optionally show debug info:
        console.log("stdout:", data.stdout);
        console.log("stderr:", data.stderr);
      } else {
        // success
        if (data.status === "ok") {
          setResult(`Predicted Price = $${data.predicted_price}`);
        } else {
          setErrorMsg("Error: " + JSON.stringify(data));
        }
      }
    } catch (err) {
      setErrorMsg(err.message || "Something went wrong");
    }
  }

  return (
    <div style={{padding: "1rem"}}>
      <h2>Crypto Prediction</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Coin: </label>
          <select value={coin} onChange={(e)=>setCoin(e.target.value)}>
            <option value="ADA">ADA</option>
            <option value="Cardano">Cardano</option>
            <option value="BTC">BTC</option>
            <option value="Bitcoin">Bitcoin</option>
            {/* etc... */}
          </select>
        </div>
        <div>
          <label>Horizon (days): </label>
          <input
            type="number"
            value={horizon}
            onChange={(e)=>setHorizon(e.target.value)}
          />
        </div>
        <div>
          <label>CMC API Key: </label>
          <input
            type="text"
            value={apiKey}
            onChange={(e)=>setApiKey(e.target.value)}
            style={{width:"300px"}}
          />
        </div>
        <button type="submit">Predict</button>
      </form>

      {/* show success or error */}
      {result && <p style={{color:"green"}}>{result}</p>}
      {errorMsg && <p style={{color:"red"}}>{errorMsg}</p>}
    </div>
  );
}

export default PredictForm;
